import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // If you want to navigate to another page after success

function SignUp() {
   const navigate = useNavigate();  // Use this to redirect to a different route after successful registration

   const [user, setUser] = useState({
       firstName: '',
       lastName: '',
       userName: '',
       email: '',
       phone: '',
       password: '',
       cpassword: ''
   });

   const [showPassword, setShowPassword] = useState(false);

   // Handling form input changes
   const handleInputs = (event) => {
       const { name, value } = event.target;
       setUser({ ...user, [name]: value });
   };

   const handleShowPassword = () => {
       setShowPassword(!showPassword);
   };

   // Handling form submission
   const postData = async (event) => {
       event.preventDefault();
       const { firstName, lastName, userName, email, phone, password, cpassword } = user;

       try {
           const response = await fetch("/register", {
               method: "POST",
               headers: {
                   "Content-Type": "application/json"
               },
               body: JSON.stringify({ firstName, lastName, userName, email, phone, password, cpassword })
           });

           const result = await response.json();

           if (response.status === 422) {
               window.alert("Email already in use");
           } else if (response.status === 401) {
               window.alert("Passwords do not match");
           } else {
               window.alert("Registered successfully");
               navigate("/login");  // Redirect to login after success
           }
       } catch (error) {
           console.log(error);
           window.alert("Failed to register");
       }
   };

   return (
       <div>
           <h1>Sign Up</h1>
           <form method="POST" onSubmit={postData}>
               <input type="text" name="firstName" placeholder="First Name" value={user.firstName} onChange={handleInputs} />
               <input type="text" name="lastName" placeholder="Last Name" value={user.lastName} onChange={handleInputs} />
               <input type="text" name="userName" placeholder="Username" value={user.userName} onChange={handleInputs} />
               <input type="email" name="email" placeholder="Email" value={user.email} onChange={handleInputs} />
               <input type="number" name="phone" placeholder="Phone Number" value={user.phone} onChange={handleInputs} />
               <input
                   type={showPassword ? "text" : "password"}
                   name="password"
                   placeholder="Password"
                   value={user.password}
                   onChange={handleInputs}
               />
               <input
                   type={showPassword ? "text" : "password"}
                   name="cpassword"
                   placeholder="Confirm Password"
                   value={user.cpassword}
                   onChange={handleInputs}
               />
               <button type="button" onClick={handleShowPassword}>
                   {showPassword ? "Hide Password" : "Show Password"}
               </button>
               <button type="submit">Sign Up</button>
           </form>
       </div>
   );
}

export default SignUp;
