      <h2>{title}</h2>
      <p>{description}</p>
      <p><strong>Published At:</strong> {new Date(publishedAt).toLocaleString()}</p> {/* Display the date here */}
      <a href={url} target="_blank" rel="noopener noreferrer">Read More</a>
    </div>
    </>
  )
}

export default NewsCard
