import React, { useEffect, useState } from 'react';
import NewsCard from './NewsCard';

function News(props) {
  const [articles, setArticles] = useState([]);
  const [search, setsearch] = useState("");
  const [query, setquery] = useState(null);
  const [loading, setloading] = useState(true);
  const [error, setError] = useState(null); // State for error handling

  const handleSearch = (e) => {
    setsearch(e.target.value);
  }

  const searchQuery = (e) => {
    e.preventDefault();
    setquery(search);
  }

  useEffect(() => {
    const getNews = async () => {
      // Construct the URL for fetching technology-related news
      let url = `https://api.mediastack.com/v1/news?access_key=aa57309daa2eb36daec4fe0253ffb88f&keywords=technology&countries=us,gb,de&limit=25`;

      try {
        setloading(true);
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();

        // Check if data and data.data exist
        if (data && data.data) {
          // Sort the articles by published_at in descending order
          const sortedArticles = data.data.sort((a, b) => new Date(b.published_at) - new Date(a.published_at));
          setArticles(sortedArticles);
        } else {
          setError("No articles found");
        }
      } catch (error) {
        console.error("Failed to fetch news:", error);
        setError(error.message);
      } finally {
        setloading(false);
      }
    };
    
    getNews();
  }, [query]);

  return (
    <>
      <h1 className="news-title text-center mt-4">Technology News</h1>
      <div className="container my-3">
        <form className="d-flex" role="search" onSubmit={searchQuery}>
          <input 
            className="form-control me-2" 
            type="search" 
            placeholder="Search latest topics" 
            aria-label="Search" 
            value={search} 
            onChange={handleSearch} 
          />
          <button className="btn btn-outline-success" type="submit">Search</button>
        </form>
      </div>
      <div className="container-fluid mt-5 flex-wrap d-flex justify-content-evenly">
        {loading ? (
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        ) : error ? (
          <div className="alert alert-danger">{error}</div> // Display error message
        ) : (
          articles.map((article, index) => (
            <NewsCard
              key={index}
              title={article.title}
              urlToImage={article.image}  // Fetching and using image URL
              url={article.url}
              description={article.description}
            />
          ))
        )}
      </div>
    </>
  );
}

export default News;
