import React, { useEffect, useState } from 'react';
import NewsCard from './NewsCard';

function News(props) {
  const [articles, setArticles] = useState([]);
  const [savedArticles, setSavedArticles] = useState([]); // New state for saved articles
  let [search, setsearch] = useState("");
  let [query, setquery] = useState(null);
  const [loading, setloading] = useState(true);
  const [preferences, setPreferences] = useState([]); // State for user preferences

  const handleSearch = (e) => {
    search = e.target.value;
    setsearch(search);
  }

  const searchQuery = (e) => {
    e.preventDefault();
    query = search;
    setquery(query);
  }

  // Fetch user preferences and personalized news articles
  useEffect(() => {
    const fetchPreferencesAndPersonalizedNews = async () => {
      try {
        const preferencesResponse = await fetch('/user-preferences', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
          credentials: 'include',
        });
        const preferencesData = await preferencesResponse.json();
        setPreferences(preferencesData.preferences); // Store user preferences

        let url = `https://newsapi.org/v2/everything?q=${preferencesData.preferences.join(" OR ")}&apiKey=your_api_key`;
        const newsResponse = await fetch(url);
        const newsData = await newsResponse.json();
        const sortedArticles = newsData.articles.sort((a, b) => new Date(b.publishedAt) - new Date(a.publishedAt));
        setArticles(sortedArticles);
      } catch (error) {
        console.log(error);
      }
    };

    fetchPreferencesAndPersonalizedNews();
  }, [preferences]);

  return (
    <>
      <h1 className="news-title text-center mt-4">{props.category ? props.category.replace('-', ' ') : 'Top Stories'}</h1>
      <div className="container my-3">
        <form className="d-flex" role="search" onSubmit={searchQuery}>
          <input className="form-control me-2" type="search" placeholder="Search latest topics" aria-label="Search" value={search} onChange={handleSearch} />
          <button className="btn btn-outline-success" type="submit">Search</button>
        </form>
      </div>
      <div className="container-fluid mt-5 flex-wrap d-flex justify-content-evenly">
        {loading ? (
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        ) : (
          articles.map((article, index) => (
            <NewsCard
              key={index}
              title={article.title}
              urlToImage={article.urlToImage}
              url={article.url}
              description={article.description}
              savedArticles={savedArticles} // Pass saved articles to NewsCard
            />
          ))
        )}
      </div>
    </>
  );
}

export default News;
