import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Optional if you want to navigate users to another page after saving
import saveIcon from '../assets/save-icon.png'; // Update with the correct path to your save icon image

function NewsCard(props) {
  const [isSaved, setIsSaved] = useState(false); // Track if the article is saved
  const navigate = useNavigate(); // Optional

  // Function to handle saving the article
  const handleSaveArticle = async () => {
    try {
      const response = await fetch('/save-article', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          title: props.title,
          url: props.url,
          urlToImage: props.urlToImage,
          description: props.description,
          publishedAt: props.publishedAt,
        }),
      });

      const data = await response.json();
      if (response.status === 201) {
        alert(data.message); // Show a success message
        setIsSaved(true); // Mark as saved
      } else {
        alert(data.message); // Show any error messages
      }
    } catch (error) {
      console.error('Error saving article:', error);
      alert('An error occurred while saving the article.');
    }
  };

  return (
    <div className="article-card">
      <img src={props.urlToImage} className="article-img" alt={props.title} />
      <div className="article-content">
        <h5>{props.title}</h5>
        <p>{props.description}</p>
        <a href={props.url} target="_blank" rel="noopener noreferrer">
          Read More
        </a>

        {/* Save Icon */}
        <img
          src={saveIcon}
          alt="Save"
          className="save-icon"
          onClick={handleSaveArticle}
          style={{
            cursor: 'pointer',
            width: '30px',
            height: '30px',
            filter: isSaved ? 'grayscale(100%)' : 'none', // Change icon style if saved
          }}
        />
        {/* Optionally display a message or change icon */}
        {isSaved && <p>Article Saved!</p>}
      </div>
    </div>
  );
}

export default NewsCard;
